import "../Style/monitor.css"
import monitor from '../Images/Monitor/Monitor.svg';
import content from '../Images/Monitor/ContentAll.svg';
import screen1 from '../Images/Monitor/Screen1.svg';
import screen2 from '../Images/Monitor/Screen2.svg';
import screen3 from '../Images/Monitor/Screen3.svg';
import React, {useState, useEffect} from 'react'



function Monitor() {



    const [scrolling, setScrolling] = useState(false);
    const [scrollTop, setScrollTop] = useState(0);

    useEffect(() => {
      function onScroll() {
        let currentPosition = window.pageYOffset; // or use document.documentElement.scrollTop;
        if (currentPosition > scrollTop || currentPosition < scrollTop) {
          // downscroll code
          console.log('poopdown')
          setScrolling(false);
        } else {
          // upscroll code
          console.log('poopup')
          setScrolling(true);
        }
        setScrollTop(currentPosition <= 0 ? 0 : currentPosition);
      }

      window.addEventListener("scroll", onScroll);
      return () => window.removeEventListener("scroll", onScroll);
    }, [scrollTop]);





    return (
        <div id="monitorAnimation">
            <p>{ scrolling }</p>
            <img id="monitor" src={monitor} alt="monitor" />
            <img id="content" src={content} alt="content" />
            <img id="screen1" src={screen1} alt="screen1" />
            <img id="screen2" src={screen2} alt="screen2" />
            <img id="screen3" src={screen3} alt="screen3" />
        </div>
    );
}

export default Monitor;
