import "../Style/intro.css"
import Monitor from '../Components/monitor';

function intro() {
    return (
        <div id="introArea">
            <div className="introLeft">
                <h1>Hello. I'm Dave</h1>
                <h2 id="introText">Iâ€™m a <span>graphic designer</span> and <span>frontend developer</span> living in Devon and I'm passionate about creating things that are beautiful both inside and out.</h2>
            <p id="introText">My work currently consists of a full time graphic design role in Exeter & building websites for independent businesses to fill out this portfolio. I find this super resourceful in terms of my learning as a web developer, as well as for keeping up with the rapid expansion of developer tools, libraries, frameworks, etc.<br/><br/>As a graphic designer I have 13+ years of experience working with Adobe Creative suite - primarily Photoshop, Illustrator and Indesign all of which I use on a daily basis. As for web development I have a certification in frontend development using Node.js, React, Bootstrap, Javascipt and CSS.<br/><br/>I look at any project or opportunity to work in a design environment as a chance to learn and develop my skillset.</p>
            </div>

            <div className="introRight">
            <Monitor />
            </div>
        </div>
    );
}

export default intro;
