import screen2 from '../Images/Monitor/Screen2.svg';
import screen3 from '../Images/Monitor/Screen3.svg';
import React, { useEffect } from 'react';
import $ from 'jquery';

function Monitor() {

    useEffect(() => {
        window.addEventListener("scroll", onScroll);
        function onScroll() {
            const scrollTop = $(window).scrollTop();
            const Offset = document.getElementById('monitor').offsetTop;
            const vh = (window.innerHeight);

            if ((scrollTop + vh) > Offset) {
                $('#content').addClass()
            }


            console.log(monitorOffset)
            console.log((window.innerHeight))
            console.log($(window).scrollTop())




        }




    }, []);




    return (
        <div id="monitorAnimation">
            <img id="monitor" src={monitor} alt="monitor" />
            <img id="content" src={content} alt="content" />
            <img id="screen1" src={screen1} alt="screen1" />
            <img id="screen2" src={screen2} alt="screen2" />
            <img id="screen3" src={screen3} alt="screen3" />
        </div>
    );
}

export default Monitor;
